INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-17 08:18:45.884000', '2023-04-22 16:18:45.884000', 0, false, 'Every object will remain at rest or in uniform motion in a straight line unless compelled to change its state by the action of an external force', false, 'First newton''s law', 5, 5);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-05 16:19:06.649000', '2023-04-22 16:19:06.649000', 0, false, 'The acceleration of an object as produced by a net force is directly proportional to the magnitude of the net force, in the same direction as the net force, and inversely proportional to the mass of the object', false, 'Second newton''s law', 5, 5);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-12 10:19:33.026000', '2023-04-22 16:19:33.026000', 0, false, 'For every action (force) in nature there is an equal and opposite reaction.', false, 'Third newton''s law', 5, 5);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-13 16:29:41.537000', '2023-04-22 16:29:41.537000', 0, false, 'Object-oriented programming (OOP) is a computer programming model that organizes software design around data, or objects, rather than functions and logic. An object can be defined as a data field that has unique attributes and behavior.OOP focuses on the objects that developers want to manipulate rather than the logic required to manipulate them. This approach to programming is well-suited for programs that are large, complex and actively updated or maintained. This includes programs for manufacturing and design, as well as mobile applications; for example, OOP can be used for manufacturing system simulation software.', false, 'OOP', 7, 7);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-16 07:30:28.358000', '2023-04-22 16:30:28.358000', 0, false, 'PostgreSQL is a powerful, open source object-relational database system with over 35 years of active development that has earned it a strong reputation for reliability, feature robustness, and performance.', false, 'Postgresql', 7, 7);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-18 09:30:57.527000', '2023-04-22 16:30:57.527000', 0, false, 'C# (pronounced "See Sharp") is a modern, object-oriented, and type-safe programming language. C# enables developers to build many types of secure and robust applications that run in .NET. C# has its roots in the C family of languages and will be immediately familiar to C, C++, Java, and JavaScript programmers', false, 'C#', 7, 7);
INSERT INTO public.post (created_at, updated_at, version, archived, content, edited, title, realisation_id, teacher_id) VALUES ('2023-04-22 16:31:20.463000', '2023-04-22 16:31:20.463000', 0, false, 'The Spring Framework provides a comprehensive programming and configuration model for modern Java-based enterprise applications - on any kind of deployment platform.A key element of Spring is infrastructural support at the application level: Spring focuses on the "plumbing" of enterprise applications so that teams can focus on application-level business logic, without unnecessary ties to specific deployment environments.', false, 'Spring/Spring boot', 7, 7);
